CREATE PROCEDURE SEL_Token  
(  
 @Token VARCHAR(20)  
)  
AS

BEGIN SET NOCOUNT ON DECLARE @idObservacao INT = NULL, @Result BIT = 0;
SELECT @idObservacao = ISNULL(idObservacao,0) FROM tbClinicas WHERE IdClinica = @idClinica;
IF @idObservacao > 0 BEGIN UPDATE tbObservacoes SET observacao = @observacoes WHERE IdObservacao = @idObservacao; END ELSE BEGIN IF @observacoes IS NOT NULL AND @observacoes <> '' BEGIN INSERT INTO tbObservacoes(observacao) VALUES(@observacoes); SET @idObservacao = SCOPE_IDENTITY(); print CONCAT(@idObservacao, ' - ', @observacoes) END; END
BEGIN TRY
UPDATE tbClinicas SET ccApelido = @ccApelido, ccRazaoSocial = @ccRazaoSocial, ccNomeFantasia = @ccNomeFantasia, ccEmail = @ccEmail, cvISS = @cvISS , cvDescontos = @cvDescontos, cbDescontoVariavel = @cbDescontoVariavel, idBanco = @idBanco, ccAlteradoPor = @UserName, cdDataAlteracao = GETDATE() , cvPgtoDias = @cvPgtoDias, idObservacao = NULLIF(@idObservacao,0), cvCNPJ = @cvCNPJ WHERE IdClinica = @idClinica;
SET @Result = 1; END TRY BEGIN CATCH INSERT INTO tbLogErro SELECT ERROR_NUMBER() AS ErrorNumber ,ERROR_SEVERITY() AS ErrorSeverity ,ERROR_STATE() AS ErrorState ,ERROR_PROCEDURE() AS ErrorProcedure ,ERROR_LINE() AS ErrorLine ,ERROR_MESSAGE() AS ErrorMessage ,GETDATE() AS Data ,@UserName AS ccCriadoPor; END CATCH
SELECT @Result; END

  BEGIN
	DECLARE @Result BIT = 0;
	BEGIN TRY
	  IF @IdEndereco IS NULL
		BEGIN
		  PRINT 'INSERT';

		  INSERT INTO tbEndereco
		  ( ccEndereco, 
			ccBairro, 
			cvCEP, 
			ccUF, 
			ccCidade, 
			idProfissional
		  )
		  VALUES
		  (@ccEndereco, @ccBairro, @cvCEP, @ccUF, @ccCidade, @idProfissional);

		  SET @Result = 1;

	  END;
		ELSE
		BEGIN
		  PRINT 'UPDATE';
		  UPDATE tbEndereco
		  SET    
			  ccEndereco = @ccEndereco, 
			  ccBairro = @ccBairro, 
			  cvCEP = @cvCEP, 
			  ccUF = @ccUF, 
			  ccCidade = @ccCidade
		   WHERE IdEndereco=@IdEndereco;

		  SET @Result = 1;
	  END;
	END TRY
	BEGIN CATCH
	  INSERT INTO tbLogErro
	  SELECT ERROR_NUMBER() AS    ErrorNumber, 
			 ERROR_SEVERITY() AS  ErrorSeverity, 
			 ERROR_STATE() AS     ErrorState, 
			 ERROR_PROCEDURE() AS ErrorProcedure, 
			 ERROR_LINE() AS      ErrorLine, 
			 ERROR_MESSAGE() AS   ErrorMessage, 
			 GETDATE() AS         Data, 
			 @UserName AS         ccCriadoPor;
	END CATCH;

	SELECT @Result;
  END;

CREATE PROCEDURE UPD_Endereco
(
   @UserName       VARCHAR(20), 
   @IdEndereco     INT          = NULL, 
   @ccEndereco     VARCHAR(MAX), 
   @ccBairro       VARCHAR(30), 
   @cvCEP          INT, 
   @ccUF           VARCHAR(2), 
   @ccCidade       VARCHAR(50), 
   @idProfissional INT
)
AS

BEGIN DECLARE @Result BIT = 0; BEGIN TRY IF @IdDadoProfissional IS NULL OR @IdDadoProfissional = '' BEGIN PRINT 'INSERT' INSERT INTO tbProfissionaisDados(idProfissional, ccFormacao, ccPosGraduacao, ccEspecialidade , ccConselho, cvNumInscricao, cvTitulo, cvTituloZona, cvTituloSecao, cvReservista, cvPIS) VALUES (@idProfissional, @ccFormacao, @ccPosGraduacao, @ccEspecialidade, @ccConselho , @cvNumInscricao, @cvTitulo, @cvTituloZona, @cvTituloSecao, @cvReservista, @cvPIS) SET @Result = 1;
END ELSE BEGIN PRINT 'UPDATE' UPDATE tbProfissionaisDados SET idProfissional=@idProfissional, ccFormacao=@ccFormacao, ccPosGraduacao=@ccPosGraduacao, ccEspecialidade=@ccEspecialidade, ccConselho=@ccConselho, cvNumInscricao=@cvNumInscricao, cvTitulo=@cvTitulo, cvTituloZona=@cvTituloZona, cvTituloSecao=@cvTituloSecao, cvReservista=@cvReservista, cvPIS=@cvPIS WHERE IdDadoProfissional= @IdDadoProfissional; SET @Result = 1; END END TRY BEGIN CATCH INSERT INTO tbLogErro SELECT ERROR_NUMBER() AS ErrorNumber ,ERROR_SEVERITY() AS ErrorSeverity ,ERROR_STATE() AS ErrorState ,ERROR_PROCEDURE() AS ErrorProcedure ,ERROR_LINE() AS ErrorLine ,ERROR_MESSAGE() AS ErrorMessage ,GETDATE() AS Data ,@UserName AS ccCriadoPor; END CATCH
SELECT @Result; END


 BEGIN  
DECLARE @Result BIT = 0;  
BEGIN TRY  
  IF @IdProfissionalBanco IS NULL OR @IdProfissionalBanco = 0   
 BEGIN  
   PRINT 'INSERT';  
   INSERT INTO tbProfissionaisBancos  
   ( ccAgencia,   
  ccConta,   
  ccOperacao,   
  idProfissional,   
  idBanco  
   )  
   VALUES  
   (@ccAgencia, @ccConta, @ccOperacao, @idProfissional, @idBanco);  

   SET @Result = 1;  

  END;  
 ELSE  
 BEGIN  
   PRINT 'UPDATE';  
   UPDATE tbProfissionaisBancos  
   SET      
    ccAgencia = @ccAgencia,   
    ccConta = @ccConta,   
    ccOperacao = @ccOperacao,   
    idProfissional = @idProfissional,   
    idBanco = @idBanco  
    WHERE IdProfissionalBanco=@IdProfissionalBanco;  

   SET @Result = 1;  
  END;  
END TRY  
BEGIN CATCH  
  INSERT INTO tbLogErro  
  SELECT ERROR_NUMBER() AS    ErrorNumber,   
   ERROR_SEVERITY() AS  ErrorSeverity,   
   ERROR_STATE() AS     ErrorState,   
   ERROR_PROCEDURE() AS ErrorProcedure,   
   ERROR_LINE() AS      ErrorLine,   
   ERROR_MESSAGE() AS   ErrorMessage,   
   GETDATE() AS         Data,   
   @UserName AS         ccCriadoPor;  
END CATCH;  

SELECT @Result;  
 END;

