CREATE PROCEDURE UPD_ProfissionalEndereco    
(    
   @UserName       VARCHAR(20),     
   @IdEndereco     INT          = NULL,     
   @ccEndereco     VARCHAR(MAX),     
   @ccBairro       VARCHAR(30),     
   @cvCEP          INT,     
   @ccUF           VARCHAR(2),     
   @ccCidade       VARCHAR(50),     
   @idProfissional INT    
)    
AS

BEGIN SET NOCOUNT ON DECLARE @idObservacao INT = NULL, @Result BIT = 0, @cvIss FLOAT(2) = 0;
IF ISNULL(@observacoes,'') <> '' BEGIN INSERT INTO tbObservacoes(observacao) VALUES(@observacoes); SELECT @idObservacao = SCOPE_IDENTITY() ; END
IF @cbIssRetido = 1 SELECT @cvIss = c.cvISS FROM tbClinicas c where c.IdClinica = @IdClinica
IF( @cvDesconto IS NULL ) BEGIN --SET @cvDesconto =(1-(ISNULL(@cvValorPago,0)/ISNULL(@cvValor,1)))*100; SET @cvDesconto = (@cvValor - (@cvValor * @cvIss / 100) - @cvValorPago) / @cvValor * 100 END ELSE BEGIN SET @cvValorPago = @cvValor-((@cvValor*@cvDesconto/100)+(@cvValor*@cvIss/100)) END BEGIN TRY UPDATE tbReceitas SET IdClinica = @IdClinica , cvValor = @cvValor , cvValorPago = @cvValorPago , cdEmissao = @cdEmissao , cdPagamento = @cdPagamento , cdRepasse = @cdRepasse , cvDesconto = @cvDesconto , idObservacao = @idObservacao , cvNF = @cvNF , cbIssRetido = @cbIssRetido WHERE idReceita = @idReceita; SET @Result = 1; END TRY BEGIN CATCH INSERT INTO tbLogErro SELECT ERROR_NUMBER() AS ErrorNumber ,ERROR_SEVERITY() AS ErrorSeverity ,ERROR_STATE() AS ErrorState ,ERROR_PROCEDURE() AS ErrorProcedure ,ERROR_LINE() AS ErrorLine ,ERROR_MESSAGE() AS ErrorMessage ,GETDATE() AS Data ,@UserName AS ccCriadoPor; END CATCH
SELECT @Result; END;
